.sidebar {
  flex: 0.35;
  background-color: rgb(44, 44, 56);
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  max-width: 300px;
  border-right: 1px solid rgb(70, 70, 70);
}
.sidebar-header {
  width: 100%;
  height: 60px;
  background-color: rgb(58, 58, 73);
  display: flex;
  align-items: center;
  justify-content: space-between;
}
.avatar {
  height: 35px;
  width: 35px;
  background: white;
  border-radius: 50%;
  margin: 5px;
}
.sidebar-search {
  height: 40px;
  display: flex;
  align-items: center;
  background: rgb(56, 56, 56);
  margin: 4px;
  margin-top: 6px;

  padding: 4px;
  border-radius: 50px;
}
.sidebar-search > .avatar {
  height: 20px;
  width: 20px;
  margin-right: 20px;
}
.sidebar-search > input {
  background-color: transparent;
  border: none;
  color: white;
  appearance: none;flex:0.9;
}
input:focus,
textarea:focus,
select:focus,
button:focus {
  outline: none;
}

.sidebar-chats {
  width: 100%;
  flex: 1;
}

.sidebar-chats > h3 {
  color: rgb(218, 218, 218);
  margin-top: 10px;
  margin-left: 10px;
  font-weight: 600;
}
.sidebar-chats > .chats {
  margin-top: 10px;
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
}
.chatbutton {
  width: 100%;
  background-color: rgb(35, 36, 53);
  border-bottom: 2px solid black;
  height: 50px;
  display: flex;
}
.chatbutton:first-of-type {
  margin-top: 10px;
}
.chatbutton-text > .chat-name {
  font-size: 16px;
  font-weight: 600;
  color: rgb(219, 219, 219);
}
.chatbutton-text{
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
}
.chatbutton-text > .lastmsg {
  font-size: 10px;
  color: gray;
}


