.chat {
  width: 100%;
  background-color: rgb(44, 44, 56);
  height: 100%;
  min-width: 300px;
  display: flex;
  flex-direction: column;
}
.chat-header {
  width: 100%;
  height: 60px;
  background-color: rgb(58, 58, 73);
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 10px;
}
.chat-header > div > p {
  font-size: 20px;
  font-weight: 600;
  color: rgb(219, 219, 219);
}
.chat-header > div {
  display: flex;
  align-items: center;
}

.chat-input {
  display: flex;
  flex-direction: row;
  align-items: center;
  width: 100%;
  background-color: rgb(58, 58, 73);
  height: 60px;
}
.chat-input {
  font-size: 25px;
}
.msg-form {
  width: 100%;
  display: flex;
}
.msg-input {
  background-color: transparent;
  border: none;
  color: white;
  appearance: none;
  font-size: 16px;
  flex: 1;
}
.msg-button {
  background-color: transparent;
  border: none;
}
.chat-msgs {
  flex: 1;
  width: 100%;
  display: flex;
  flex-direction: column-reverse;
}
.msg-cont {
  width: 100%;
  display: flex;
  margin-bottom: 5px;
}
.message {
  width: fit-content;
  padding: 5px;
  padding-left: 10px;
  padding-right: 10px;
  max-width: 70%;
  border-radius: 10px;
  margin-left: 4px;
  margin-right: 4px;
  color: white;
  display: block;
  padding-bottom: 5px;
}
.message>.text {
  margin-right: 44px;
}
.sent {
  justify-content: flex-end;
}
.recieved {
  justify-content: flex-start;
}

.msg-sent {
  background-color: rgb(22, 90, 65);
  text-align: right;
  display: flex;
  align-items: flex-end;
  justify-content: flex-end;
  min-width: 50px;

}
.msg-recieved {
  background-color: rgb(65, 62, 62);
  text-align: left;
  text-align: right;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-start;
  min-width: 50px;
}
.msg-sent > p {
  text-align: right;
}

.msg-recieved > p {
  text-align: left;
}
.message > .name {
  position: absolute;
  font-size: 13px;
  color: rgb(151, 151, 151);
}
.message > .createdAt {
  position: absolute;
  font-size: 11px;
  color: rgb(151, 151, 151);
}
.msg-sent > .name {
  transform: translateY(-28px) translateX(5px);
  text-align: right;
}
.msg-recieved > .name {
  transform: translateY(-25px) translateX(-5px);
  text-align: right;
}
.msg-sent > .createdAt {
  transform: translateY(5px) translateX(5px);
  text-align: right;
}
.msg-recieved > .createdAt {
  transform: translateY(12px) translateX(5px);
  text-align: right;
  align-self: flex-end;
}

